/*DROP TEMPORARY TABLE IF EXISTS gq0;DROP TEMPORARY TABLE IF EXISTS gq1;DROP TEMPORARY TABLE IF EXISTS gq2;DROP TEMPORARY TABLE IF EXISTS gq3;*//*CREATE TEMPORARY TABLE gq0 AS (  SELECT v.id, 0 edge_order  FROM   vertex v  WHERE  v.id = 48175  AND    v.txn_end IS NULL);*//*CREATE INDEX eindx ON edge (v_in);CREATE INDEX eoutdx ON edge (v_out);*/CREATE TEMPORARY TABLE gq1 AS (  SELECT v.id, e.edge_order  FROM   vertex v, edge e    /*, gq0 g*/  WHERE  e.v_in = 48175      /*  g.id */  AND    e.v_out = v.id  AND    e.label = 'isPartOf'  AND    e.txn_end IS NULL  AND    v.txn_end IS NULL);CREATE TEMPORARY TABLE gq2 AS (  SELECT v.id, e.edge_order   FROM   vertex v, edge e, gq1 g  WHERE  e.v_in = g.id  AND    e.v_out = v.id  AND    e.label = 'isCopyOf'  AND    e.txn_end IS NULL  AND    v.txn_end IS NULL);CREATE TEMPORARY TABLE gq3 AS (  SELECT v.id, e.edge_order   FROM   vertex v, edge e, gq2 g  WHERE  e.v_in = g.id  AND    e.v_out = v.id  AND    e.label = 'isFileOf'  AND    e.txn_end IS NULL  AND    v.txn_end IS NULL);/*SELECT *FROM   gq0UNION*//*SELECT *FROM   gq1UNIONSELECT *FROM   gq2UNIONSELECT *FROM   gq3;*/INSERT INTO gq3 (id, edge_order)SELECT id, edge_order FROM   gq2;INSERT INTO gq3 (id, edge_order)SELECT id, edge_order FROM   gq1;SELECT p.*, cast(p.value as char(100)) FROM property p, gq3WHERE p.id = gq3.idAND name = 'type'AND p.txn_end IS NULL;